mapper.select:
SELECT notice_id as noticeId, title, write_datetime as writeDatetime, content, view_count as viewCount
FROM notice
WHERE notice_id = #{noticeId}

mapper.update:
UPDATE notice SET
    title = #{title},
    write_datetime = #{writeDatetime},
    content = #{content},
    view_count = #{viewCount}
WHERE notice_id = #{noticeId}
